import React, { useState, useEffect } from 'react';
import {
    View,
    Image,
    ImageBackground,
    Text,
    ActivityIndicator,
    TextInput,
    TouchableOpacity,
    SafeAreaView,
} from 'react-native';
import styles from './styles';

function Login({ displayed }) {
    // const [value, onChangeText] = React.useState('Placeholder');

    return (<>
        <SafeAreaView style={styles.container}>
            <ImageBackground source={require('../../../assets/images/lettuce1.jpg')} style={styles.image}>
                {/* <Text style={styles.text}>User Name</Text>
                <TextInput style={styles.input}
                    onChangeText={text => onChangeText(text)}
                    value={value} />
                <Text style={styles.text}>Password</Text>
                <TextInput style={styles.input}
                    onChangeText={text => onChangeText(text)}
                    value={value}/> */}
                {displayed && <View style={styles.back}>
                    <Text style={styles.title}>LOGIN</Text>
                    {/* <ActivityIndicator
                        style={styles.activityIndicator}
                    ></ActivityIndicator> */}
                    <View>
                        <Text style={styles.Username}>Username</Text>
                        <View style={styles.inputNameStack}>
                            <SafeAreaView style={styles.input}>
                            <TextInput
                                placeholder="Username"
                                style={styles.placeholder}
                            ></TextInput>
                            </SafeAreaView>
                        </View>
                    </View>
                    <View>
                        <Text style={styles.password}>Password</Text>
                        <View style={styles.inputPasswordStack}>
                            <SafeAreaView style={styles.input}>
                            <TextInput
                                secureTextEntry={true} 
                                placeholder="Password"
                                style={styles.placeholder}
                            ></TextInput>
                            </SafeAreaView>
                        </View>
                    </View>
                    <TouchableOpacity
                        onPress={() => props.navigation.navigate("Login")}
                        style={styles.button}
                    >
                        <Text style={styles.submit}>Submit</Text>
                    </TouchableOpacity>
                </View>
                }
            </ImageBackground>
        </SafeAreaView>
    </>)
}

export default Login
