import React, { useState } from 'react';
import { StyleSheet, View, ImageBackground, Dimensions, SafeAreaView } from 'react-native';
import { Login, Navbar, Test } from './src/components';

export default function App() {
  const [displayed, setSide] = useState(false);

  const handleSide=()=> setSide(!displayed);

  return (<>
    <View style={styles.container}>

      <Navbar onDisplaySide={handleSide}/>
      <Login displayed={displayed}/>
      {/* <Test/> */}
    </View>

  </>);
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  image: {
    flex: 1,
    justifyContent: "center",
    width: Dimensions.get('window').width,
    height: Dimensions.get('window').height
  }
});
